"use client"

import { useState, useEffect } from "react"
import { CreditCard, Search, Loader2, AlertCircle, CheckCircle } from "lucide-react"
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const PaymentSection = () => {
  const [payments, setPayments] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [modalOpen, setModalOpen] = useState(false)
  const [selectedOrder, setSelectedOrder] = useState(null)
  const [pointsToUse, setPointsToUse] = useState(0)

  const loadPayments = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:8080/api/payments", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })

      if (!response.ok) {
        throw new Error("Failed to fetch payments")
      }

      const data = await response.json()
      setPayments(data)
      setError(null)
    } catch (err) {
      console.error("Error loading payments:", err)
      setError("Failed to load payments. Please try again later.")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadPayments()
  }, [])

  // Filter payments based on search term
  const filteredPayments = payments.filter(
    (payment) =>
      payment.OrderID?.toString().includes(searchTerm) ||
      payment.TableID?.toString().includes(searchTerm) ||
      payment.FullName?.toLowerCase().includes(searchTerm.toLowerCase()),
  )

  // Separate unpaid and paid orders
  const unpaidOrders = filteredPayments.filter((payment) => payment.PaymentID === null)
  const paidOrders = filteredPayments.filter((payment) => payment.PaymentID !== null)

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("vi-VN", {
      style: "currency",
      currency: "VND",
    }).format(amount)
  }

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return new Intl.DateTimeFormat("vi-VN", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
    }).format(date)
  }

  const handlePaymentClick = (order) => {
    setSelectedOrder(order)
    setPointsToUse(0)
    setModalOpen(true)
  }

  const handlePaymentSubmit = async () => {
    try {
      // Here you would implement the actual payment processing
      // For example:
      // const response = await fetch("http://localhost:8080/api/payments/process", {
      //   method: "POST",
      //   headers: {
      //     "Content-Type": "application/json",
      //   },
      //   body: JSON.stringify({
      //     orderId: selectedOrder.OrderID,
      //     userId: selectedOrder.UserID,
      //     pointsUsed: pointsToUse,
      //     amount: selectedOrder.TotalPrice - (pointsToUse * 1000) // Assuming 1 point = 1000 VND
      //   }),
      // })

      // Close the modal and refresh data
      setModalOpen(false)
      loadPayments()
    } catch (err) {
      console.error("Error processing payment:", err)
      setError("Failed to process payment. Please try again.")
    }
  }

  return (
    <div className="space-y-6">
      <DashboardSection title="Quản lý thanh toán" icon={<CreditCard className="h-5 w-5" />}>
        {error && (
          <div className="error-alert">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        <div className="order-search-container">
          <Search className="order-search-icon h-4 w-4" />
          <input
            type="text"
            placeholder="Tìm kiếm theo mã đơn hàng, bàn hoặc tên khách hàng..."
            className="order-search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={loadPayments} className="refresh-button">
            Làm mới
          </button>
        </div>

        {loading ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Đang tải dữ liệu thanh toán...</span>
          </div>
        ) : (
          <>
            {/* Unpaid Orders Section */}
            <div className="mb-8">
              <h3 className="text-lg font-semibold mb-4 text-gray-800 border-b pb-2">Đơn hàng chưa thanh toán</h3>
              {unpaidOrders.length === 0 ? (
                <div className="empty-state">
                  <p>Không có đơn hàng chưa thanh toán.</p>
                </div>
              ) : (
                <div className="overflow-x-auto w-full">
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Mã đơn</th>
                        <th>Bàn</th>
                        <th>Khách hàng</th>
                        <th>Ngày đặt</th>
                        <th>Tổng tiền</th>
                        <th>Thao tác</th>
                      </tr>
                    </thead>
                    <tbody>
                      {unpaidOrders.map((order) => (
                        <tr key={order.OrderID}>
                          <td>{order.OrderID}</td>
                          <td>{order.TableID}</td>
                          <td>{order.FullName}</td>
                          <td>{formatDate(order.OrderDate)}</td>
                          <td className="font-medium">{formatCurrency(order.TotalPrice)}</td>
                          <td>
                            <button onClick={() => handlePaymentClick(order)} className="serve-button">
                              <CheckCircle className="h-4 w-4 mr-1" />
                              Thanh toán
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            {/* Paid Orders Section */}
            <div>
              <h3 className="text-lg font-semibold mb-4 text-gray-800 border-b pb-2">Đơn hàng đã thanh toán</h3>
              {paidOrders.length === 0 ? (
                <div className="empty-state">
                  <p>Không có đơn hàng đã thanh toán.</p>
                </div>
              ) : (
                <div className="overflow-x-auto w-full">
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Mã thanh toán</th>
                        <th>Mã đơn</th>
                        <th>Bàn</th>
                        <th>Khách hàng</th>
                        <th>Ngày đặt</th>
                        <th>Tổng tiền</th>
                      </tr>
                    </thead>
                    <tbody>
                      {paidOrders.map((order) => (
                        <tr key={order.OrderID}>
                          <td>{order.PaymentID}</td>
                          <td>{order.OrderID}</td>
                          <td>{order.TableID}</td>
                          <td>{order.FullName}</td>
                          <td>{formatDate(order.OrderDate)}</td>
                          <td className="font-medium">{formatCurrency(order.TotalPrice)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </>
        )}
      </DashboardSection>

      {/* Payment Modal */}
      {modalOpen && selectedOrder && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Thanh toán đơn hàng #{selectedOrder.OrderID}</h3>

            <div className="mb-4">
              <p className="text-sm text-gray-600 mb-1">Khách hàng: {selectedOrder.FullName}</p>
              <p className="text-sm text-gray-600 mb-1">Điểm hiện có: {selectedOrder.Points} điểm</p>
              <p className="text-sm text-gray-600 mb-3">Giá trị đơn hàng: {formatCurrency(selectedOrder.TotalPrice)}</p>

              <div className="mt-4">
                <label htmlFor="pointsInput" className="block text-sm font-medium text-gray-700 mb-1">
                  Nhập số điểm muốn sử dụng:
                </label>
                <input
                  id="pointsInput"
                  type="number"
                  min="0"
                  max={Math.min(selectedOrder.Points, Math.floor(selectedOrder.TotalPrice / 1000))}
                  value={pointsToUse}
                  onChange={(e) => setPointsToUse(Number(e.target.value) || 0)}
                  className="form-input w-full border border-gray-300 rounded-md px-3 py-2"
                />
                <p className="text-xs text-gray-500 mt-1">
                  1 điểm = 1.000đ (Tối đa: {Math.min(selectedOrder.Points, Math.floor(selectedOrder.TotalPrice / 1000))}{" "}
                  điểm)
                </p>
              </div>

              <div className="mt-4 p-3 bg-gray-50 rounded-md">
                <p className="text-sm font-medium">Tổng thanh toán sau khi sử dụng điểm:</p>
                <p className="text-lg font-bold text-green-600">
                  {formatCurrency(Math.max(0, selectedOrder.TotalPrice - pointsToUse * 1000))}
                </p>
              </div>
            </div>

            <div className="flex justify-end space-x-3 mt-6">
              <button
                onClick={() => setModalOpen(false)}
                className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
              >
                Hủy
              </button>
              <button
                onClick={handlePaymentSubmit}
                className="px-4 py-2 bg-indigo-600 rounded-md text-sm font-medium text-white hover:bg-indigo-700"
              >
                Xác nhận thanh toán
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default PaymentSection
