import React from 'react';
import { Users, BookOpen, ShoppingBag, CreditCard, Home } from 'lucide-react';

const Sidebar = ({ onSelect, selectedPage, isOpen }) => {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: <Home className="h-5 w-5" /> },
    { id: 'user', label: 'Users', icon: <Users className="h-5 w-5" /> },
    { id: 'menu', label: 'Menu', icon: <BookOpen className="h-5 w-5" /> },
    { id: 'order', label: 'Orders', icon: <ShoppingBag className="h-5 w-5" /> },
    { id: 'payment', label: 'Payments', icon: <CreditCard className="h-5 w-5" /> },
  ];

  return (
    <div className={`sidebar fixed inset-y-0 left-0 transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0 transition duration-200 ease-in-out z-30 lg:static lg:inset-0`}>
      <div className="flex flex-col h-full bg-gray-800 text-white shadow-lg w-64">
        <div className="px-4 py-6 border-b border-gray-700">
          <h2 className="text-2xl font-bold text-center text-white">RMS</h2>
        </div>

        <nav className="flex-1 px-2 py-4 space-y-1">
          {menuItems.map((item) => (
            <a
              key={item.id}
              href="#"
              onClick={(e) => {
                e.preventDefault();
                onSelect(item.id);
              }}
              className={`flex items-center px-4 py-3 text-sm rounded-lg transition-colors ${
                selectedPage === item.id
                  ? 'bg-primary text-white'
                  : 'text-gray-300 hover:bg-gray-700 hover:text-white'
              }`}
            >
              <span className="mr-3">{item.icon}</span>
              <span>{item.label}</span>
              {selectedPage === item.id && (
                <span className="ml-auto h-2 w-2 rounded-full bg-white"></span>
              )}
            </a>
          ))}
        </nav>

        <div className="p-4 border-t border-gray-700">
          <div className="bg-gray-700 rounded-lg p-3">
            <p className="text-sm text-gray-300">Need help?</p>
            <a href="#" className="text-sm font-medium text-primary hover:underline">
              Contact Support
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
