"use client"

import { useState, useEffect } from "react"
import "./Content.css"

const MenuManager = ({ icon }) => {
  const [menuData, setMenuData] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  // Function to fetch menu data from the API
  const fetchData = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:8080/api/buffets/menu",{
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.status}`)
      }

      const data = await response.json()
      setMenuData(data.setList || [])
      setError(null)
    } catch (err) {
      console.error("Failed to fetch menu data:", err)
      setError("Failed to load menu data. Please try again later.")
    } finally {
      setLoading(false)
    }
  }

  // Fetch data when component mounts
  useEffect(() => {
    fetchData()
  }, [])

  // Function to get the correct image path based on the set name
  const getImagePath = (setName) => {
    if (setName === "Hotpot") {
      return "./assets/Buffet_Lau.png"
    } else if (setName === "Seafood Hotpot") {
      return "./assets/Buffet_Lau_Hai_San.png"
    } else if (setName === "BBQ") {
      return "./assets/Buffet_BBQ.png"
    } else if (setName.startsWith("Sashimi")) {
      return `./assets/Buffet_${setName}.png`
    } else {
      // Default case
      return `./assets/Buffet_${setName.replace(/\s+/g, "_")}.png`
    }
  }

  return (
    <div className="dashboard-section">
      <div className="section-header">
        <div className="section-title">
          {icon && <span className="section-icon">{icon}</span>}
          <h2 className="text-lg font-medium text-gray-900">Menu</h2>
        </div>
        <button onClick={fetchData} className="refresh-button">
          Refresh Menu
        </button>
      </div>

      <div className="section-content">
        {loading ? (
          <div className="loading-container">
            <svg
              className="animate-spin h-8 w-8 text-indigo-600"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
              ></path>
            </svg>
          </div>
        ) : error ? (
          <div className="error-alert">
            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
              <path
                fillRule="evenodd"
                d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                clipRule="evenodd"
              ></path>
            </svg>
            <span>{error}</span>
          </div>
        ) : menuData.length === 0 ? (
          <div className="empty-state">
            <p>No menu items found.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {menuData.map((set) => (
              <div key={set.id} className="dashboard-card card-gradient-indigo">
                <div className="card-content">
                  <div className="card-info">
                    <h3>{set.Name}</h3>
                    <p className="text-sm text-gray-500">Set ID: {set.id}</p>
                    <p className="text-sm text-gray-500">
                      {set.Dishes ? `${set.Dishes.length} dishes` : "No dishes available"}
                    </p>
                  </div>
                  <div className="w-24 h-24 overflow-hidden rounded-lg">
                    <img
                      src={getImagePath(set.Name) || "/placeholder.svg"}
                      alt={set.Name}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        e.target.onerror = null
                        e.target.src = "./assets/Buffet_Lau.png"
                      }}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  )
}

export default MenuManager
