"use client"

import { useState, useEffect } from "react"
import { Loader2, AlertCircle, CreditCard, Search } from 'lucide-react'
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const PaymentSection = () => {
  const [payments, setPayments] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")

  const loadPayments = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:8080/api/payments", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })

      if (!response.ok) {
        throw new Error("Failed to fetch payments")
      }

      const data = await response.json()
      setPayments(data)
      setError(null)
    } catch (err) {
      console.error("Error loading payments:", err)
      setError("Failed to load payments. Please try again later.")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadPayments()
  }, [])

  // Filter payments based on search term
  const filteredPayments = payments.filter(
    (payment) =>
      payment.TableID?.toString().includes(searchTerm) ||
      payment.Amount?.toString().includes(searchTerm)
  )

  return (
    <div className="space-y-6">
      <DashboardSection title="Quản lý thanh toán" icon={<CreditCard className="h-5 w-5" />}>
        {error && (
          <div className="error-alert">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        <div className="order-search-container">
          <Search className="order-search-icon h-4 w-4" />
          <input
            type="text"
            placeholder="Tìm kiếm theo mã bàn hoặc số tiền..."
            className="order-search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button 
            onClick={() => {
              setLoading(true)
              loadPayments()
            }} 
            className="refresh-button"
          >
            Làm mới
          </button>
        </div>

        {loading ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Đang tải dữ liệu thanh toán...</span>
          </div>
        ) : (
          <>
            {filteredPayments.length === 0 ? (
              <div className="empty-state">
                <p>Không tìm thấy dữ liệu thanh toán.</p>
              </div>
            ) : (
              <div className="overflow-x-auto w-full">
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>Mã thanh toán</th>
                      <th>Mã bàn</th>
                      <th>Số tiền</th>
                      <th>Ngày thanh toán</th>
                      <th>Trạng thái</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredPayments.map((payment) => (
                      <tr key={payment.PaymentID}>
                        <td>{payment.PaymentID}</td>
                        <td>{payment.TableID}</td>
                        <td className="font-medium">{payment.Amount?.toLocaleString()} VND</td>
                        <td>{new Date(payment.PaymentDate).toLocaleString()}</td>
                        <td>
                          <span className={`order-status ${
                            payment.Status === "Completed" ? "status-completed" : 
                            payment.Status === "Pending" ? "status-pending" : "status-progress"
                          }`}>
                            {payment.Status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </>
        )}
      </DashboardSection>
    </div>
  )
}

export default PaymentSection
