"use client"

import { useEffect, useState } from "react"
import {
  Loader2,
  CheckCircle,
  PlusCircle,
  AlertCircle,
  ShoppingBag,
  DollarSign,
  Coffee,
  Clock,
  TrendingUp,
  Users,
  ArrowUpRight,
  BookOpen,
  CreditCard,
  Settings,
  Search,
} from "lucide-react"
import "./Content.css"

const DashboardSection = ({ title, children, icon }) => (
  <div className="dashboard-section">
    <div className="section-header">
      <div className="section-title">
        {icon && <span className="section-icon">{icon}</span>}
        <h2 className="text-lg font-medium text-gray-900">{title}</h2>
      </div>
    </div>
    <div className="section-content">{children}</div>
  </div>
)

const DashboardCard = ({ title, value, change, icon, color, bgColor }) => (
  <div className={`dashboard-card ${bgColor}`}>
    <div className="card-content">
      <div className="card-info">
        <p className="text-sm font-medium text-gray-500">{title}</p>
        <h3>{value}</h3>
        <div className={`card-trend ${change.startsWith("+") ? "trend-up" : "trend-down"}`}>
          {change.startsWith("+") ? (
            <TrendingUp className="h-3 w-3 mr-1" />
          ) : (
            <ArrowUpRight className="h-3 w-3 mr-1 transform rotate-90" />
          )}
          {change}
        </div>
      </div>
      <div className={`card-icon ${color}`}>{icon}</div>
    </div>
  </div>
)

const Dashboard = () => (
  <div className="space-y-6">
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 dashboard-grid">
      <DashboardCard
        title="Total Orders"
        value="156"
        change="+12.5%"
        icon={<ShoppingBag className="h-6 w-6 text-indigo-600" />}
        color="bg-indigo-100"
        bgColor="card-gradient-indigo"
      />
      <DashboardCard
        title="Total Revenue"
        value="$4,320"
        change="+8.2%"
        icon={<DollarSign className="h-6 w-6 text-green-600" />}
        color="bg-green-100"
        bgColor="card-gradient-green"
      />
      <DashboardCard
        title="Active Tables"
        value="12/20"
        change="-2"
        icon={<Coffee className="h-6 w-6 text-blue-600" />}
        color="bg-blue-100"
        bgColor="card-gradient-blue"
      />
      <DashboardCard
        title="Pending Orders"
        value="8"
        change="+3"
        icon={<Clock className="h-6 w-6 text-amber-600" />}
        color="bg-amber-100"
        bgColor="card-gradient-amber"
      />
    </div>

    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
      <div className="lg:col-span-2">
        <DashboardSection title="Recent Orders" icon={<ShoppingBag className="h-5 w-5" />}>
          <div className="space-y-4">
            <div className="order-item">
              <div className="order-info">
                <div className="order-icon">
                  <Coffee className="h-5 w-5" />
                </div>
                <div className="order-details">
                  <p>Table A1</p>
                  <p className="order-meta">2 items • 10 mins ago</p>
                </div>
              </div>
              <span className="order-status status-pending">Pending</span>
            </div>

            <div className="order-item">
              <div className="order-info">
                <div className="order-icon">
                  <Coffee className="h-5 w-5" />
                </div>
                <div className="order-details">
                  <p>Table B2</p>
                  <p className="order-meta">1 item • 15 mins ago</p>
                </div>
              </div>
              <span className="order-status status-completed">Completed</span>
            </div>

            <div className="order-item">
              <div className="order-info">
                <div className="order-icon">
                  <Coffee className="h-5 w-5" />
                </div>
                <div className="order-details">
                  <p>Table C3</p>
                  <p className="order-meta">3 items • 20 mins ago</p>
                </div>
              </div>
              <span className="order-status status-progress">In Progress</span>
            </div>
          </div>
        </DashboardSection>
      </div>

      <div>
        <DashboardSection title="Active Staff" icon={<Users className="h-5 w-5" />}>
          <div className="space-y-4">
            <div className="staff-item">
              <div className="staff-avatar avatar-indigo">
                <span>JD</span>
              </div>
              <div className="staff-info">
                <p>John Doe</p>
                <p className="staff-meta">Waiter • On shift</p>
              </div>
            </div>

            <div className="staff-item">
              <div className="staff-avatar avatar-green">
                <span>AS</span>
              </div>
              <div className="staff-info">
                <p>Alice Smith</p>
                <p className="staff-meta">Chef • On shift</p>
              </div>
            </div>

            <div className="staff-item">
              <div className="staff-avatar avatar-amber">
                <span>RJ</span>
              </div>
              <div className="staff-info">
                <p>Robert Johnson</p>
                <p className="staff-meta">Manager • On shift</p>
              </div>
            </div>
          </div>
        </DashboardSection>
      </div>
    </div>
  </div>
)

const Content = ({ selectedPage }) => {
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [servedQuantities, setServedQuantities] = useState({})
  const [newOrderName, setNewOrderName] = useState("")

//   const loadOrders = async () => {
//     setLoading(true)
//     try {
//       // Simulate API call
//       setTimeout(() => {
//         const mockOrders = [
//           { OrderID: 1, DishID: 101, TableID: "A1", DishName: "Burger", RemainingQuantity: 2 },
//           { OrderID: 2, DishID: 102, TableID: "B2", DishName: "Pizza", RemainingQuantity: 1 },
//           { OrderID: 3, DishID: 103, TableID: "C3", DishName: "Pasta", RemainingQuantity: 3 },
//         ]
//         setOrders(mockOrders)
//         setLoading(false)
//       }, 500)
//     } catch (err) {
//       setError("Failed to load orders.")
//       setLoading(false)
//     }
//   }

    const loadOrders = async () => {
        setLoading(true); // Bắt đầu tải
        try {
            const response = await fetch('http://localhost:8080/api/orders/serving', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            console.log('Orders:', data);
            setOrders(data);
        } catch (error) {
            console.error('Error fetching orders:', error);
        } finally {
            setLoading(false); // Kết thúc tải
        }
    };

  useEffect(() => {
    if (selectedPage === "order") {
      loadOrders()
    }
  }, [selectedPage])

  const handleQuantityChange = (orderID, dishID, quantity) => {
    setServedQuantities((prev) => ({
      ...prev,
      [`${orderID}-${dishID}`]: quantity,
    }))
  }

  const served = async (orderID, dishID, quantity) => {
    // Simulate API call
    setOrders((prev) =>
      prev.map((order) => {
        if (order.OrderID === orderID && order.DishID === dishID) {
          return { ...order, RemainingQuantity: order.RemainingQuantity - quantity }
        }
        return order
      }),
    )
    setServedQuantities((prev) => {
      const { [`${orderID}-${dishID}`]: removed, ...rest } = prev
      return rest
    })
  }

  const addOrder = () => {
    // Simulate adding an order
    alert(`Adding order: ${newOrderName}`)
    setNewOrderName("")
  }

  switch (selectedPage) {
    case "dashboard":
      return <Dashboard />
    case "user":
      return <DashboardSection title="User Management" icon={<Users className="h-5 w-5" />} />
    case "menu":
      return <DashboardSection title="Menu Management" icon={<BookOpen className="h-5 w-5" />} />
    case "order":
      return (
        <div className="space-y-6">
          <DashboardSection title="Order Management" icon={<ShoppingBag className="h-5 w-5" />}>
            {error && (
              <div className="error-alert">
                <AlertCircle className="h-5 w-5" />
                {error}
              </div>
            )}

            <div className="order-search-container">
              <Search className="order-search-icon h-4 w-4" />
              <input type="text" placeholder="Search orders by table or dish name..." className="order-search-input" />
            </div>

            {loading ? (
              <div className="loading-container">
                <Loader2 className="h-8 w-8 loading-spinner" />
                <span className="ml-2 text-gray-600">Loading orders...</span>
              </div>
            ) : (
              <>
                {orders.length === 0 ? (
                  <div className="empty-state">
                    <p>No orders to serve at the moment.</p>
                    <button onClick={loadOrders} className="refresh-button">
                      Refresh
                    </button>
                  </div>
                ) : (
                  <div className="overflow-x-auto w-full">
                    <table className="orders-table">
                      <thead>
                        <tr>
                          <th>Table ID</th>
                          <th>Dish Name</th>
                          <th>Remaining</th>
                          <th>Quantity to Serve</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {orders.map((order, index) => (
                          <tr key={index}>
                            <td>{order.TableID}</td>
                            <td className="font-medium">{order.DishName}</td>
                            <td>
                              <span className="quantity-badge">{order.RemainingQuantity}</span>
                            </td>
                            <td>
                              <input
                                type="number"
                                min="1"
                                max={order.RemainingQuantity}
                                value={servedQuantities[`${order.OrderID}-${order.DishID}`] || ""}
                                onChange={(e) => handleQuantityChange(order.OrderID, order.DishID, e.target.value)}
                                placeholder="Qty"
                                className="quantity-input"
                              />
                            </td>
                            <td>
                              <button
                                onClick={() =>
                                  served(
                                    order.OrderID,
                                    order.DishID,
                                    Number.parseInt(servedQuantities[`${order.OrderID}-${order.DishID}`] || 0, 10),
                                  )
                                }
                                disabled={
                                  !servedQuantities[`${order.OrderID}-${order.DishID}`] ||
                                  servedQuantities[`${order.OrderID}-${order.DishID}`] > order.RemainingQuantity
                                }
                                className="serve-button"
                              >
                                <CheckCircle className="h-4 w-4" />
                                Serve
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </>
            )}
          </DashboardSection>

          <DashboardSection title="Add New Order" icon={<PlusCircle className="h-5 w-5" />}>
            <div className="add-order-form">
              <div className="form-group">
                <label htmlFor="orderName" className="form-label">
                  Order Name
                </label>
                <input
                  id="orderName"
                  type="text"
                  value={newOrderName}
                  onChange={(e) => setNewOrderName(e.target.value)}
                  placeholder="Enter order name"
                  className="form-input"
                />
              </div>
              <button onClick={addOrder} className="add-button">
                <PlusCircle className="h-4 w-4" />
                Add Order
              </button>
            </div>
          </DashboardSection>
        </div>
      )
    case "payment":
      return <DashboardSection title="Payment Management" icon={<CreditCard className="h-5 w-5" />} />
    case "settings":
      return <DashboardSection title="Settings" icon={<Settings className="h-5 w-5" />} />
    default:
      return <Dashboard />
  }
}

export default Content
