"use client"

import { useState } from "react"
import { Loader2, AlertCircle, FileText, Search, BarChart } from "lucide-react"
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const ReportSection = () => {
  const [reportData, setReportData] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedMonth, setSelectedMonth] = useState("")
  const [selectedYear, setSelectedYear] = useState("2025")

  const fetchReport = async () => {
    if (!selectedMonth) {
      setError("Vui lòng chọn tháng để tạo báo cáo")
      return
    }

    try {
      setLoading(true)
      setError(null)

      const response = await fetch("http://localhost:8080/api/payments/report", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          month: Number.parseInt(selectedMonth),
          year: Number.parseInt(selectedYear),
        }),
      })

      if (!response.ok) {
        throw new Error("Failed to generate report")
      }

      const data = await response.json()
      setReportData(data.report || [])
    } catch (err) {
      console.error("Error generating report:", err)
      setError("Không thể tạo báo cáo. Vui lòng thử lại sau.")
    } finally {
      setLoading(false)
    }
  }

  // Filter report data based on search term
  const filteredReportData = reportData.filter(
    (item) =>
      item.DishName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      item.Category?.toLowerCase().includes(searchTerm.toLowerCase()),
  )

  // Generate months for select box
  const months = [
    { value: "1", label: "Tháng 1" },
    { value: "2", label: "Tháng 2" },
    { value: "3", label: "Tháng 3" },
    { value: "4", label: "Tháng 4" },
    { value: "5", label: "Tháng 5" },
    { value: "6", label: "Tháng 6" },
    { value: "7", label: "Tháng 7" },
    { value: "8", label: "Tháng 8" },
    { value: "9", label: "Tháng 9" },
    { value: "10", label: "Tháng 10" },
    { value: "11", label: "Tháng 11" },
    { value: "12", label: "Tháng 12" },
  ]

  // Generate years for select box (current year and 5 years back)
  const currentYear = new Date().getFullYear()
  const years = Array.from({ length: 6 }, (_, i) => {
    const yearValue = currentYear - 5 + i
    return { value: yearValue.toString(), label: yearValue.toString() }
  })

  const fetchReports = async () => {
    await fetchReport()
  }

  return (
    <div className="space-y-6">
      <DashboardSection title="Báo cáo" icon={<BarChart className="h-5 w-5" />}>
        <div className="mb-6">
          <div className="flex flex-wrap items-end gap-4">
            <div className="form-group w-auto min-w-[150px]">
              <label htmlFor="month" className="form-label">
                Tháng
              </label>
              <select
                id="month"
                className="form-input w-full"
                value={selectedMonth}
                onChange={(e) => setSelectedMonth(e.target.value)}
              >
                <option value="">-- Chọn tháng --</option>
                {months.map((month) => (
                  <option key={month.value} value={month.value}>
                    {month.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group w-auto min-w-[120px]">
              <label htmlFor="year" className="form-label">
                Năm
              </label>
              <select
                id="year"
                className="form-input w-full"
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
              >
                {years.map((year) => (
                  <option key={year.value} value={year.value}>
                    {year.label}
                  </option>
                ))}
              </select>
            </div>

            <button
              onClick={fetchReports}
              className="refresh-button h-[38px] px-6"
              disabled={!selectedMonth || !selectedYear}
            >
              {loading ? <Loader2 className="h-4 w-4 mr-2 animate-spin" /> : <FileText className="h-4 w-4 mr-2" />}
              Tạo báo cáo
            </button>
          </div>
        </div>

        {error && (
          <div className="error-alert mb-4">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        {reportData.length > 0 && (
          <div className="order-search-container">
            <Search className="order-search-icon h-4 w-4" />
            <input
              type="text"
              placeholder="Tìm kiếm theo tên món hoặc danh mục..."
              className="order-search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        )}

        {loading ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Đang tạo báo cáo...</span>
          </div>
        ) : (
          <>
            {reportData.length === 0 ? (
              <div className="empty-state">
                <p>Chưa có dữ liệu báo cáo. Vui lòng chọn tháng và năm để tạo báo cáo.</p>
              </div>
            ) : (
              <div className="overflow-x-auto w-full">
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>Tên món</th>
                      <th>Danh mục</th>
                      <th>Tổng số lượng đã đặt</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredReportData.map((item, index) => (
                      <tr key={index}>
                        <td className="font-medium">{item.DishName}</td>
                        <td>{item.Category}</td>
                        <td>
                          <span className="quantity-badge">{item.TotalQuantityOrdered}</span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </>
        )}
      </DashboardSection>
    </div>
  )
}

export default ReportSection
