"use client"

import { useEffect, useState } from "react"
import { Loader2, AlertCircle, CreditCard, Search, CheckCircle } from "lucide-react"
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const PaymentSection = () => {
  const [payments, setPayments] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [dialogOpen, setDialogOpen] = useState(false)
  const [selectedPayment, setSelectedPayment] = useState(null)
  const [pointsToUse, setPointsToUse] = useState(0)

  const fetchPayments = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/payments/all")
      if (!response.ok) {
        throw new Error("Failed to fetch payments")
      }
      const data = await response.json()
      setPayments(data.payments || [])
      setError(null)
    } catch (err) {
      console.error("Error fetching payments:", err)
      setError("Failed to load payments. Please try again later.")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    // Initial fetch
    fetchPayments()

    // Set up interval for continuous fetching
    const intervalId = setInterval(() => {
      fetchPayments()
    }, 1000)

    // Clean up interval on component unmount
    return () => clearInterval(intervalId)
  }, [])

  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return date.toLocaleString("vi-VN", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    })
  }

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("vi-VN", {
      style: "currency",
      currency: "VND",
    }).format(amount)
  }

  const handlePaymentClick = (payment) => {
    if (payment.UserID !== -1) {
      // Registered user
      setSelectedPayment(payment)
      setPointsToUse(0)
      setDialogOpen(true)
    } else {
      // Guest user
      handleGuestPayment(payment)
    }
  }

  const handleGuestPayment = async (payment) => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:8080/api/payments/handleGuess", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          orderId: payment.OrderID,
          amount: payment.TotalPrice,
          paymentMethod: "cash",
        }),
      })

      if (!response.ok) {
        throw new Error("Failed to process payment")
      }

      // Refresh payments after successful payment
      fetchPayments()
      alert("Payment processed successfully!")
    } catch (err) {
      console.error("Error processing payment:", err)
      alert("Failed to process payment. Please try again.")
    } finally {
      setLoading(false)
    }
  }

  const handleUserPayment = async () => {
    if (!selectedPayment) return

    try {
      setLoading(true)

      // Calculate new amount after points deduction
      const newAmount = Math.max(0, selectedPayment.TotalPrice - pointsToUse * 1000)

      const response = await fetch("http://localhost:8080/api/payments/handleUser", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userId: selectedPayment.UserID,
          orderId: selectedPayment.OrderID,
          usingPoints: pointsToUse > 0 ? 1 : 0,
          pointsUsedNumber: pointsToUse,
          amount: newAmount,
          paymentMethod: "cash",
        }),
      })

      if (!response.ok) {
        throw new Error("Failed to process payment")
      }

      // Close dialog and refresh payments
      setDialogOpen(false)
      fetchPayments()
      alert("Payment processed successfully!")
    } catch (err) {
      console.error("Error processing payment:", err)
      alert("Failed to process payment. Please try again.")
    } finally {
      setLoading(false)
    }
  }

  // Filter payments based on search term
  const filteredPayments = payments.filter(
    (payment) =>
      payment.TableID.toString().includes(searchTerm) ||
      payment.UserName.toLowerCase().includes(searchTerm.toLowerCase()),
  )

  // Separate unpaid and paid payments
  const unpaidPayments = filteredPayments.filter((payment) => payment.TotalPrice === 0)
  const paidPayments = filteredPayments.filter((payment) => payment.TotalPrice > 0)

  return (
    <div className="space-y-6">
      {/* Payment Dialog */}
      {dialogOpen && selectedPayment && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Payment Details</h3>

            <div className="mb-4">
              <p className="text-sm text-gray-600">Customer: {selectedPayment.UserName}</p>
              <p className="text-sm text-gray-600">Table: {selectedPayment.TableID}</p>
              <p className="text-sm text-gray-600">Order ID: {selectedPayment.OrderID}</p>
              <p className="text-sm text-gray-600">Total Amount: {formatCurrency(selectedPayment.TotalPrice)}</p>
              <p className="text-sm font-medium text-indigo-600">Available Points: {selectedPayment.Points}</p>
            </div>

            <div className="mb-6">
              <label htmlFor="points" className="block text-sm font-medium text-gray-700 mb-1">
                Points to Use (1 point = 1,000 VND)
              </label>
              <input
                type="number"
                id="points"
                min="0"
                max={Math.min(selectedPayment.Points, Math.floor(selectedPayment.TotalPrice / 1000))}
                value={pointsToUse}
                onChange={(e) => setPointsToUse(Number.parseInt(e.target.value) || 0)}
                className="form-input w-full"
              />

              <div className="mt-2 p-2 bg-gray-50 rounded-md">
                <p className="text-sm font-medium">
                  New Amount: {formatCurrency(Math.max(0, selectedPayment.TotalPrice - pointsToUse * 1000))}
                </p>
                <p className="text-xs text-gray-500">Points Discount: {formatCurrency(pointsToUse * 1000)}</p>
              </div>
            </div>

            <div className="flex justify-end space-x-3">
              <button
                onClick={() => setDialogOpen(false)}
                className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 hover:bg-gray-50"
              >
                Cancel
              </button>
              <button
                onClick={handleUserPayment}
                className="px-4 py-2 bg-indigo-600 rounded-md text-sm font-medium text-white hover:bg-indigo-700"
              >
                Process Payment
              </button>
            </div>
          </div>
        </div>
      )}

      <DashboardSection title="Payment Management" icon={<CreditCard className="h-5 w-5" />}>
        {error && (
          <div className="error-alert">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        <div className="order-search-container">
          <Search className="order-search-icon h-4 w-4" />
          <input
            type="text"
            placeholder="Search by table or customer name..."
            className="order-search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {loading && payments.length === 0 ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Loading payments...</span>
          </div>
        ) : (
          <>
            {/* Unpaid Payments Section */}
            <div className="mb-8">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Unpaid Orders</h3>

              {unpaidPayments.length === 0 ? (
                <div className="empty-state">
                  <p>No unpaid orders found.</p>
                </div>
              ) : (
                <div className="overflow-x-auto w-full">
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Order ID</th>
                        <th>Table</th>
                        <th>Date</th>
                        <th>Customer</th>
                        <th>Points</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {unpaidPayments.map((payment) => (
                        <tr key={payment.PaymentID}>
                          <td>{payment.OrderID}</td>
                          <td>{payment.TableID}</td>
                          <td>{formatDate(payment.OrderDate)}</td>
                          <td>{payment.UserID !== -1 ? payment.UserName : "Guest"}</td>
                          <td>{payment.UserID !== -1 ? payment.Points : "N/A"}</td>
                          <td>
                            <button
                              onClick={() => handlePaymentClick(payment)}
                              className="px-3 py-1 bg-indigo-600 rounded-md text-sm font-medium text-white hover:bg-indigo-700"
                            >
                              Payment
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>

            {/* Paid Payments Section */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Paid Orders</h3>

              {paidPayments.length === 0 ? (
                <div className="empty-state">
                  <p>No paid orders found.</p>
                </div>
              ) : (
                <div className="overflow-x-auto w-full">
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Order ID</th>
                        <th>Table</th>
                        <th>Date</th>
                        <th>Customer</th>
                        <th>Amount</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {paidPayments.map((payment) => (
                        <tr key={payment.PaymentID}>
                          <td>{payment.OrderID}</td>
                          <td>{payment.TableID}</td>
                          <td>{formatDate(payment.OrderDate)}</td>
                          <td>{payment.UserID !== -1 ? payment.UserName : "Guest"}</td>
                          <td>{formatCurrency(payment.TotalPrice)}</td>
                          <td>
                            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
                              <CheckCircle className="h-3 w-3 mr-1" />
                              Paid
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </>
        )}
      </DashboardSection>
    </div>
  )
}

export default PaymentSection
