"use client"

import { useState, useEffect } from "react"
import { Loader2, AlertCircle, FileText, Search } from "lucide-react"
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const ReportSection = () => {
  const [reports, setReports] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedMonth, setSelectedMonth] = useState("")
  const [selectedYear, setSelectedYear] = useState("2025")

  const months = [
    { value: "1", label: "Tháng 1" },
    { value: "2", label: "Tháng 2" },
    { value: "3", label: "Tháng 3" },
    { value: "4", label: "Tháng 4" },
    { value: "5", label: "Tháng 5" },
    { value: "6", label: "Tháng 6" },
    { value: "7", label: "Tháng 7" },
    { value: "8", label: "Tháng 8" },
    { value: "9", label: "Tháng 9" },
    { value: "10", label: "Tháng 10" },
    { value: "11", label: "Tháng 11" },
    { value: "12", label: "Tháng 12" },
  ]

  const years = [
    { value: "2023", label: "2023" },
    { value: "2024", label: "2024" },
    { value: "2025", label: "2025" },
    { value: "2026", label: "2026" },
  ]

  const fetchReports = async () => {
    if (!selectedMonth || !selectedYear) {
      setError("Vui lòng chọn cả tháng và năm để xem báo cáo")
      return
    }

    try {
      setLoading(true)
      setError(null)

      const response = await fetch("http://localhost:8080/api/payments/report", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          month: Number.parseInt(selectedMonth),
          year: Number.parseInt(selectedYear),
        }),
      })

      if (!response.ok) {
        throw new Error("Failed to fetch reports")
      }

      const data = await response.json()
      setReports(data)
    } catch (err) {
      console.error("Error fetching reports:", err)
      setError("Không thể tải báo cáo. Vui lòng thử lại sau.")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    if (selectedMonth && selectedYear) {
      fetchReports()
    }
  }, [selectedMonth, selectedYear])

  // Filter reports based on search term
  const filteredReports = reports.filter(
    (report) =>
      report.TableID?.toString().includes(searchTerm) ||
      report.Amount?.toString().includes(searchTerm) ||
      report.PaymentDate?.includes(searchTerm),
  )

  return (
    <div className="space-y-6">
      <DashboardSection title="Báo cáo" icon={<FileText className="h-5 w-5" />}>
        {error && (
          <div className="error-alert">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        <div className="mb-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="form-group">
              <label htmlFor="month" className="form-label">
                Chọn tháng
              </label>
              <select
                id="month"
                className="form-input"
                value={selectedMonth}
                onChange={(e) => setSelectedMonth(e.target.value)}
              >
                <option value="">-- Chọn tháng --</option>
                {months.map((month) => (
                  <option key={month.value} value={month.value}>
                    {month.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="year" className="form-label">
                Chọn năm
              </label>
              <select
                id="year"
                className="form-input"
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
              >
                <option value="">-- Chọn năm --</option>
                {years.map((year) => (
                  <option key={year.value} value={year.value}>
                    {year.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group flex items-end">
              <button
                onClick={fetchReports}
                className="refresh-button w-full"
                disabled={!selectedMonth || !selectedYear}
              >
                Tạo báo cáo
              </button>
            </div>
          </div>
        </div>

        <div className="order-search-container">
          <Search className="order-search-icon h-4 w-4" />
          <input
            type="text"
            placeholder="Tìm kiếm trong báo cáo..."
            className="order-search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {loading ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Đang tải báo cáo...</span>
          </div>
        ) : (
          <>
            {!selectedMonth || !selectedYear ? (
              <div className="empty-state">
                <p>Vui lòng chọn tháng và năm để xem báo cáo</p>
              </div>
            ) : filteredReports.length === 0 ? (
              <div className="empty-state">
                <p>Không có dữ liệu báo cáo cho thời gian đã chọn.</p>
              </div>
            ) : (
              <div className="overflow-x-auto w-full">
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>Mã thanh toán</th>
                      <th>Mã bàn</th>
                      <th>Số tiền</th>
                      <th>Ngày thanh toán</th>
                      <th>Trạng thái</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredReports.map((report, index) => (
                      <tr key={index}>
                        <td>{report.PaymentID}</td>
                        <td>{report.TableID}</td>
                        <td className="font-medium">{report.Amount?.toLocaleString()} VND</td>
                        <td>{new Date(report.PaymentDate).toLocaleString()}</td>
                        <td>
                          <span
                            className={`order-status ${
                              report.Status === "Completed"
                                ? "status-completed"
                                : report.Status === "Pending"
                                  ? "status-pending"
                                  : "status-progress"
                            }`}
                          >
                            {report.Status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </>
        )}
      </DashboardSection>
    </div>
  )
}

export default ReportSection
