"use client"

import { useEffect, useState } from "react"
import { Loader2, CheckCircle, PlusCircle, AlertCircle } from "lucide-react"

const Content = ({ selectedPage }) => {
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(false)
  const [newOrderName, setNewOrderName] = useState("")
  const [servedQuantities, setServedQuantities] = useState({})
  const [error, setError] = useState(null)

  useEffect(() => {
    if (selectedPage === "order") {
      loadOrders()
    }
  }, [selectedPage])

  const loadOrders = async () => {
    setLoading(true)
    setError(null)
    try {
      const response = await fetch("http://localhost:8080/api/orders/serving", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })
      if (!response.ok) {
        throw new Error("Network response was not ok")
      }
      const data = await response.json()
      console.log("Orders:", data)
      setOrders(data)
    } catch (error) {
      console.error("Error fetching orders:", error)
      setError("Failed to load orders. Please try again.")
    } finally {
      setLoading(false)
    }
  }

  const served = async (orderId, dishId, quantity) => {
    try {
      const response = await fetch("http://localhost:8080/api/orders/served", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          orderId,
          dishId,
          quantity,
        }),
      })

      if (!response.ok) {
        throw new Error("Failed to update served status")
      }

      await loadOrders()
      // Clear the input after successful serving
      setServedQuantities((prev) => {
        const newState = { ...prev }
        delete newState[`${orderId}-${dishId}`]
        return newState
      })

      // Use a more subtle notification instead of alert
      showNotification(`Dish served successfully!`, "success")
    } catch (error) {
      console.error("Error updating served status:", error)
      showNotification("Failed to mark dish as served. Please try again.", "error")
    }
  }

  const showNotification = (message, type = "info") => {
    // This is a simple implementation - in a real app, you might use a toast library
    const notification = document.createElement("div")
    notification.className = `notification ${type}`
    notification.textContent = message
    document.body.appendChild(notification)

    setTimeout(() => {
      notification.classList.add("fade-out")
      setTimeout(() => document.body.removeChild(notification), 500)
    }, 3000)
  }

  const handleQuantityChange = (orderId, dishId, value) => {
    setServedQuantities((prev) => ({
      ...prev,
      [`${orderId}-${dishId}`]: value,
    }))
  }

  const addOrder = async () => {
    if (!newOrderName.trim()) {
      showNotification("Please enter an order name.", "warning")
      return
    }

    const newOrder = { name: newOrderName, status: "pending" }
    try {
      const response = await fetch("http://localhost:8080/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newOrder),
      })
      if (!response.ok) {
        throw new Error("Failed to add order")
      }
      setNewOrderName("")
      loadOrders()
      showNotification(`Order "${newOrderName}" has been added!`, "success")
    } catch (error) {
      console.error("Error adding order:", error)
      showNotification("Failed to add order.", "error")
    }
  }

  const renderContent = () => {
    switch (selectedPage) {
      case "user":
        return <DashboardSection title="User Management" />
      case "menu":
        return <DashboardSection title="Menu Management" />
      case "order":
        return (
          <div className="space-y-6">
            <DashboardSection title="Order Management">
              {error && (
                <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-md flex items-center mb-4">
                  <AlertCircle className="h-5 w-5 mr-2" />
                  {error}
                </div>
              )}

              {loading ? (
                <div className="flex justify-center items-center py-12">
                  <Loader2 className="h-8 w-8 animate-spin text-primary" />
                  <span className="ml-2 text-gray-600">Loading orders...</span>
                </div>
              ) : (
                <>
                  {orders.length === 0 ? (
                    <div className="text-center py-12 bg-gray-50 rounded-lg">
                      <p className="text-gray-500">No orders to serve at the moment.</p>
                      <button
                        onClick={loadOrders}
                        className="mt-4 px-4 py-2 bg-primary text-white rounded-md hover:bg-primary/90 transition-colors"
                      >
                        Refresh
                      </button>
                    </div>
                  ) : (
                    <div className="overflow-x-auto">
                      <table className="w-full border-collapse">
                        <thead>
                          <tr className="bg-gray-100">
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-600 border-b">Table ID</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-600 border-b">
                              Dish Name
                            </th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-600 border-b">
                              Remaining
                            </th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-600 border-b">
                              Quantity to Serve
                            </th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-600 border-b">Action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {orders.map((order, index) => (
                            <tr
                              key={index}
                              className={`border-b hover:bg-gray-50 transition-colors ${
                                index % 2 === 0 ? "bg-white" : "bg-gray-50"
                              }`}
                            >
                              <td className="px-4 py-3 text-sm text-gray-900">{order.TableID}</td>
                              <td className="px-4 py-3 text-sm text-gray-900 font-medium">{order.DishName}</td>
                              <td className="px-4 py-3 text-sm text-gray-900">
                                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                  {order.RemainingQuantity}
                                </span>
                              </td>
                              <td className="px-4 py-3 text-sm">
                                <input
                                  type="number"
                                  min="1"
                                  max={order.RemainingQuantity}
                                  value={servedQuantities[`${order.OrderID}-${order.DishID}`] || ""}
                                  onChange={(e) => handleQuantityChange(order.OrderID, order.DishID, e.target.value)}
                                  placeholder="Qty"
                                  className="w-20 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                                />
                              </td>
                              <td className="px-4 py-3 text-sm">
                                <button
                                  onClick={() =>
                                    served(
                                      order.OrderID,
                                      order.DishID,
                                      Number.parseInt(servedQuantities[`${order.OrderID}-${order.DishID}`] || 0, 10),
                                    )
                                  }
                                  disabled={
                                    !servedQuantities[`${order.OrderID}-${order.DishID}`] ||
                                    servedQuantities[`${order.OrderID}-${order.DishID}`] > order.RemainingQuantity
                                  }
                                  className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                                >
                                  <CheckCircle className="h-4 w-4 mr-1" />
                                  Serve
                                </button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                </>
              )}
            </DashboardSection>

            <DashboardSection title="Add New Order">
              <div className="flex items-end gap-4">
                <div className="flex-1">
                  <label htmlFor="orderName" className="block text-sm font-medium text-gray-700 mb-1">
                    Order Name
                  </label>
                  <input
                    id="orderName"
                    type="text"
                    value={newOrderName}
                    onChange={(e) => setNewOrderName(e.target.value)}
                    placeholder="Enter order name"
                    className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent"
                  />
                </div>
                <button
                  onClick={addOrder}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-primary hover:bg-primary/90 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary transition-colors"
                >
                  <PlusCircle className="h-4 w-4 mr-1" />
                  Add Order
                </button>
              </div>
            </DashboardSection>
          </div>
        )
      case "payment":
        return <DashboardSection title="Payment Management" />
      default:
        return (
          <div className="text-center py-12">
            <h1 className="text-3xl font-bold text-gray-900">Welcome to the Order Management System</h1>
            <p className="mt-4 text-gray-600">Select a menu item to get started.</p>
          </div>
        )
    }
  }

  return (
    <>
      <div className="content">{renderContent()}</div>
      <style jsx global>{`
                .notification {
                    position: fixed;
                    bottom: 20px;
                    right: 20px;
                    padding: 12px 20px;
                    border-radius: 4px;
                    color: white;
                    font-weight: 500;
                    z-index: 1000;
                    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
                    animation: slide-in 0.3s ease-out;
                }
                
                .notification.success {
                    background-color: #10b981;
                }
                
                .notification.error {
                    background-color: #ef4444;
                }
                
                .notification.warning {
                    background-color: #f59e0b;
                }
                
                .notification.info {
                    background-color: #3b82f6;
                }
                
                .notification.fade-out {
                    animation: fade-out 0.5s ease-out forwards;
                }
                
                @keyframes slide-in {
                    from { transform: translateX(100%); opacity: 0; }
                    to { transform: translateX(0); opacity: 1; }
                }
                
                @keyframes fade-out {
                    from { opacity: 1; }
                    to { opacity: 0; }
                }
            `}</style>
    </>
  )
}

// Helper component for consistent section styling
const DashboardSection = ({ title, children }) => (
  <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
    <div className="px-6 py-4 border-b border-gray-200">
      <h2 className="text-lg font-medium text-gray-900">{title}</h2>
    </div>
    <div className="px-6 py-5">{children}</div>
  </div>
)

export default Content
