"use client"

import { useState, useEffect } from "react"
import { CreditCard, Search, Loader2, AlertCircle } from "lucide-react"
import "./Content.css"
import DashboardSection from "./Dashboard/DashboardSection"

const PaymentSection = () => {
  const [payments, setPayments] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [searchTerm, setSearchTerm] = useState("")

  const loadPayments = async () => {
    try {
      setLoading(true)
      const response = await fetch("http://localhost:8080/api/payments", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      })

      if (!response.ok) {
        throw new Error("Failed to fetch payments")
      }

      const data = await response.json()
      setPayments(data)
      setError(null)
    } catch (err) {
      console.error("Error loading payments:", err)
      setError("Failed to load payments. Please try again later.")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadPayments()
  }, [])

  // Filter payments based on search term
  const filteredPayments = payments.filter(
    (payment) => payment.OrderID?.toString().includes(searchTerm) || payment.TableID?.toString().includes(searchTerm),
  )

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat("vi-VN", {
      style: "currency",
      currency: "VND",
    }).format(amount)
  }

  // Format date
  const formatDate = (dateString) => {
    const date = new Date(dateString)
    return new Intl.DateTimeFormat("vi-VN", {
      year: "numeric",
      month: "2-digit",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
    }).format(date)
  }

  return (
    <div className="space-y-6">
      <DashboardSection title="Quản lý thanh toán" icon={<CreditCard className="h-5 w-5" />}>
        {error && (
          <div className="error-alert">
            <AlertCircle className="h-5 w-5" />
            {error}
          </div>
        )}

        <div className="order-search-container">
          <Search className="order-search-icon h-4 w-4" />
          <input
            type="text"
            placeholder="Tìm kiếm theo mã đơn hàng hoặc bàn..."
            className="order-search-input"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={loadPayments} className="refresh-button">
            Làm mới
          </button>
        </div>

        {loading ? (
          <div className="loading-container">
            <Loader2 className="h-8 w-8 loading-spinner" />
            <span className="ml-2 text-gray-600">Đang tải dữ liệu thanh toán...</span>
          </div>
        ) : (
          <>
            {filteredPayments.length === 0 ? (
              <div className="empty-state">
                <p>Không tìm thấy dữ liệu thanh toán.</p>
              </div>
            ) : (
              <div className="overflow-x-auto w-full">
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>Mã thanh toán</th>
                      <th>Mã đơn hàng</th>
                      <th>Bàn</th>
                      <th>Tổng tiền</th>
                      <th>Ngày thanh toán</th>
                      <th>Trạng thái</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredPayments.map((payment) => (
                      <tr key={payment.PaymentID}>
                        <td>{payment.PaymentID}</td>
                        <td>{payment.OrderID}</td>
                        <td>{payment.TableID}</td>
                        <td className="font-medium">{formatCurrency(payment.Amount)}</td>
                        <td>{formatDate(payment.PaymentDate)}</td>
                        <td>
                          <span
                            className={`order-status ${
                              payment.Status === "Completed"
                                ? "status-completed"
                                : payment.Status === "Pending"
                                  ? "status-pending"
                                  : "status-progress"
                            }`}
                          >
                            {payment.Status === "Completed"
                              ? "Hoàn thành"
                              : payment.Status === "Pending"
                                ? "Chờ xử lý"
                                : "Đang xử lý"}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </>
        )}
      </DashboardSection>
    </div>
  )
}

export default PaymentSection
