"use client";

import { useState, useEffect, useMemo } from "react";
import { useSpring, animated } from "@react-spring/web"; // Thêm react-spring
import "./Content.css";

const MenuManager = ({ icon }) => {
  const [menuData, setMenuData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedSet, setExpandedSet] = useState(null); // Trạng thái mở rộng

  // Hàm lấy dữ liệu menu từ API
  const fetchData = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:8080/api/buffets/menu", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error(`Lỗi: ${response.status}`);
      }

      const data = await response.json();
      setMenuData(data.setList || []);
      setError(null);
    } catch (err) {
      console.error("Lấy dữ liệu menu thất bại:", err);
      setError("Không thể tải dữ liệu menu. Vui lòng thử lại sau.");
    } finally {
      setLoading(false);
    }
  };

  // Lấy dữ liệu khi component được mount
  useEffect(() => {
    fetchData();
  }, []);

  // Hàm lấy đường dẫn ảnh dựa trên tên set
  const getImagePath = useMemo(() => {
    return (setName) => {
      console.log(setName);
      if (setName === "Hotpot") {
        return "/assets/Buffet_Lau.png";
      } else if (setName === "Seafood Hotpot") {
        return "/assets/Buffet_Lau_Hai_San.png";
      } else if (setName === "BBQ") {
        return "/assets/Buffet_BBQ.png";
      } else if (setName.startsWith("Sashimi")) {
        const formattedName = setName.replace(/\s+/g, "_");
        console.log(`/assets/Buffet_${formattedName}.png`);
        return `/assets/Buffet_${formattedName}.png`;
      } else {
        const formattedName = setName.replace(/\s+/g, "_");
        console.log(`/assets/Buffet_${formattedName}.png`);
        return `/assets/Buffet_${formattedName}.png`;
      }
    };
  }, []);

  // Hàm dịch Category sang tiếng Việt và đếm số món còn lại
  const translateCategory = (category) => {
    const translations = {
      Appetizer: "Khai vị",
      Main: "Món chính",
      Dessert: "Tráng miệng",
    };
    return translations[category] || category;
  };

  // Hàm đếm số món theo category
  const countDishesByCategory = (dishes, category) => {
    return dishes.filter((dish) => dish.Category === category).length;
  };

  // Hàm xử lý nhấp để mở rộng/đóng mục menu
  const handleToggleExpand = (setId) => {
    setExpandedSet(expandedSet === setId ? null : setId); // Toggle trạng thái
  };

  return (
    <div className="dashboard-section">
      <div className="section-header">
        <div className="section-title">
          {icon && <span className="section-icon">{icon}</span>}
          <h2 className="text-lg font-medium text-gray-900">Menu</h2>
        </div>
        <button onClick={fetchData} className="refresh-button">
          Làm mới Menu
        </button>
      </div>

      <div className="section-content">
        {loading ? (
          <div className="loading-container">
            <svg
              className="animate-spin h-8 w-8 text-indigo-600"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              ></circle>
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
              ></path>
            </svg>
          </div>
        ) : error ? (
          <div className="error-alert">
            <svg
              className="w-5 h-5"
              fill="currentColor"
              viewBox="0 0 20 20"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fillRule="evenodd"
                d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z"
                clipRule="evenodd"
              ></path>
            </svg>
            <span>{error}</span>
          </div>
        ) : menuData.length === 0 ? (
          <div className="empty-state">
            <p>Không tìm thấy mục menu.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {menuData.map((set) => {
              // Tính số món còn lại (giả sử hiển thị "Còn 0...")
              const remainingDishes = set.Dishes ? set.Dishes.length : 0;

              // Hiệu ứng mở rộng bằng react-spring
              const springProps = useSpring({
                from: { height: 0, opacity: 0 },
                to: {
                  height: expandedSet === set.id ? "auto" : 0,
                  opacity: expandedSet === set.id ? 1 : 0,
                },
                config: { tension: 200, friction: 20 }, // Điều chỉnh độ mượt của animation
              });

              return (
                <div
                  key={set.id}
                  className="dashboard-card card-gradient-indigo"
                >
                  <div
                    className="card-content cursor-pointer"
                    onClick={() => handleToggleExpand(set.id)}
                  >
                    <div className="card-info">
                      <h3>{set.Name}</h3>
                      <p className="text-sm text-gray-500">ID Set: {set.id}</p>
                      <p className="text-sm text-gray-500">
                        {set.Dishes
                          ? `${set.Dishes.length} món`
                          : "Không có món nào"}
                      </p>
                    </div>
                    <div className="w-24 h-24 overflow-hidden rounded-lg">
                      <img
                        src={getImagePath(set.Name) || "/assets/default_set.png"}
                        alt={set.Name}
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          console.log(`Tải ảnh thất bại: ${e.target.src}`);
                          e.target.onerror = null;
                          e.target.src = "/assets/default_set.png";
                        }}
                      />
                    </div>
                  </div>

                  {/* Phần mở rộng với hiệu ứng trượt */}
                  {set.Dishes && set.Dishes.length > 0 && (
                    <animated.div
                      style={{
                        ...springProps,
                        overflow: "hidden",
                      }}
                    >
                      <div className="dishes-list p-4 bg-white border border-gray-200 rounded-lg shadow-md mt-2">
                        {/* Header với số lượng món còn lại */}
                        <div className="flex justify-between items-center mb-3">
                          <div></div> {/* Để trống bên trái cho giống ảnh */}
                          <div className="flex items-center space-x-2">
                            <span className="text-sm font-medium text-gray-700">
                              Còn {remainingDishes}...
                            </span>
                            <svg
                              className={`w-4 h-4 transform transition-transform ${
                                expandedSet === set.id ? "rotate-180" : ""
                              }`}
                              fill="none"
                              stroke="currentColor"
                              viewBox="0 0 24 24"
                              xmlns="http://www.w3.org/2000/svg"
                            >
                              <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M19 9l-7 7-7-7"
                              />
                            </svg>
                          </div>
                        </div>

                        {/* Danh sách các loại món ăn */}
                        <div className="space-y-2">
                          <div className="text-base font-medium text-gray-700">
                            Khai vị
                          </div>
                          <div className="text-base font-medium text-gray-700">
                            Món chính
                          </div>
                          <div className="text-base font-medium text-gray-700">
                            Tráng miệng
                          </div>
                        </div>
                      </div>
                    </animated.div>
                  )}
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default MenuManager;
